<template>
	<view>
		<cu-custom bgColor="bg-gradual-orange" class="status_bar">
			<block slot="content">{{pname}}</block>
		</cu-custom>
		<view class="content" @touchstart="hideDrawer">
			<scroll-view class="msg-list" scroll-y="true" :scroll-with-animation="scrollAnimation" :scroll-top="scrollTop"
			 :scroll-into-view="scrollToView" @scrolltoupper="loadHistory" upper-threshold="50">

				<view class="chatmar">

				</view>
				<view class="row" v-for="(row,index) in msgList" :key="index" :id="'msg'+row.msg.id">

					<block v-if="row.type=='user'">
						<!-- 自己发出的消息 -->
						<view class="my" v-if="row.msg.userinfo.uid==myuid">
							<!-- 左-消息 -->
							<view class="left">
								<!-- 文字消息 -->
								<view v-if="row.msg.type=='text'" class="bubble">
									<rich-text :nodes="row.msg.content.text" class="msgfont"></rich-text>
								</view>
								<!-- 语言消息 -->
								<!-- <view v-if="row.msg.type=='voice'" class="bubble voice" @tap="playVoice(row.msg)" :class="playMsgid == row.msg.id?'play':''">
									<view class="length">{{row.msg.content.length}}</view>
									<view class="icon my-voice"></view>
								</view> -->
								<!-- 图片消息 -->
								<view v-if="row.msg.type=='img'" class="bubble img" @tap="showPic(row.msg)">
									<image :src="row.msg.content.url" :style="{'width': row.msg.content.w+'px','height': row.msg.content.h+'px'}"></image>
								</view>


							</view>
							<!-- 右-头像 -->
							<view class="right">
								<image :src="myavatar"></image>
							</view>
						</view>
						<!-- 别人发出的消息 -->
						<view class="other" v-if="row.msg.userinfo.uid!=myuid">
							<!-- 左-头像 -->
							<view class="left">
								<image :src="pavatar"></image>
							</view>
							<!-- 右-用户名称-时间-消息 -->
							<view class="right">
								<!-- <view class="username">
									<view class="name">{{Pname}}</view>
									<view class="time">{{row.msg.time}}</view>
								</view> -->
								<!-- 文字消息 -->
								<view v-if="row.msg.type=='text'" class="bubble">
									<rich-text :nodes="row.msg.content.text" class="msgfont"></rich-text>
								</view>

								<!-- 图片消息 -->
								<view v-if="row.msg.type=='img'" class="bubble img" @tap="showPic(row.msg)">
									<image :src="row.msg.content.url" :style="{'width': row.msg.content.w+'px','height': row.msg.content.h+'px'}"></image>
								</view>

							</view>
						</view>
					</block>
				</view>
			</scroll-view>
		</view>
		<!-- 抽屉栏 -->
		<view class="popup-layer" :class="popupLayerClass">

			<!-- 已选习惯 -->
			<view class="list" :class="{hidden:hideEmoji}">
				<view class="docenter">
					<scroll-view class="scroll" scroll-y="true">
						<view v-for="(item,index) in xuguanlist" v-if="isselecthabit[item.iconname]" :key="index" class="flexicon">
							<image :src="'../../../static/iconfont/'+item.url" mode="" class="imglist" @tap="daka(item)" @longpress="delXG(item)"></image>
							<text class="iconname">{{item.iconname}}</text>
						</view>
					</scroll-view>
				</view>
			</view>
			<!-- 更多功能 相册-拍照-红包 -->
			<view class="more-layer" :class="{hidden:hideMore}">
				<view class="list">

					<view class="docenter">
						<scroll-view class="scroll" scroll-y="true">
							<view v-for="(item,index) in xuguanlist" v-if="!(isselecthabit[item.iconname])" :key="index" class="flexicon">
								<!-- <image v-if="item.isselect" :src="'../../../static/iconfont/'+item.url" mode="" class="imglist"></image> -->
								<image :src="'../../../static/iconfont/'+item.url" mode="" class="imglist" @tap="addxiguan(item)"></image>
								<text class="iconname">{{item.iconname}}</text>
								<!-- <text v-if="item.isselect" class="iconname">{{item.iconname}}</text> -->
							</view>
						</scroll-view>
					</view>
				</view>
			</view>
		</view>
		<!-- 底部输入栏 -->
		<view class="input-box" :class="popupLayerClass">
			<!-- H5下不能录音，输入栏布局改动一下 -->
			<!-- #ifndef H5 -->
			<!-- <view class="voice">
				<view class="icon" :class="isVoice?'jianpan':'yuyin'" @tap="switchVoice"></view>
			</view> -->
			<!-- #endif -->
			<!-- #ifdef H5 -->
			<view class="more" @tap="showMore">
				<view class="icon add"></view>
			</view>
			<!-- #endif -->
			<view class="textbox">
				<!-- <view class="voice-mode" :class="[isVoice?'':'hidden',recording?'recording':'']" @touchstart="voiceBegin" @touchmove.stop.prevent="voiceIng" @touchend="voiceEnd" @touchcancel="voiceCancel">{{voiceTis}}</view> -->
				<view class="text-mode" :class="isVoice?'hidden':''">
					<view class="xg-selected" @tap="chooseXG">
						<!-- <view class="icon biaoqing"></view> -->
						<view class="icon" style="padding-top: 5px;">
							<image class="xgchoose-icon" :src="selectedXG"></image>
						</view>
					</view>
					<view class="box" @tap="chooseXG()">
						<textarea disabled="true" auto-height="true" v-model="textMsg" @focus="textareaFocus" />
						</view>
					<view v-show="textMsg==''?false:true" class="uni-icon uni-icon-clear clearinput" @tap="clearInput"></view>
				</view>
			</view>
			<!-- #ifndef H5 -->
			<view class="more" @tap="showMore">
				<view class="icon add"></view>
			</view>
			<!-- #endif -->
			<view class="send" :class="isVoice?'hidden':''" @tap="sendText">
				<view class="btn">发送</view>
			</view>
		</view>

	</view>
</template>
<script>
	
	import store from '../../../store/index.js'
	var _self;
	export default {
		data() {
			return {
				//我的头像
				myavatar: "",
				//Ta的头像
				pavatar:"",
				pname:"",
				//未选图标列表
				xuguanlist: [],
				//已选习惯的图标
				selectedXG:"../../../static/daka.png",
				//文字消息
				textMsg:'',
				//消息列表
				isHistoryLoading:false,
				scrollAnimation:false,
				scrollTop:0,
				scrollToView:'',
				msgList:[],
				msgImgList:[],
				myuid:0,
				// yuliao:"",
				yuliao:[],
				// 抽屉参数
				popupLayerClass:'',
				// more参数
				hideMore:true,
				//表情定义
				hideEmoji:true,
				uuid:"",
				isselecthabit:{},
				pname: "", //姓名
				// tempsex: "Ta",
				// psex: "", //性别
				// pcallme: "", //Ta对你的昵称
				pavatar: "../../static/touxiang/avatar.png",
				// //
				myname: "",
				// mysex: "",
				// myIdentity: "",
				// mybirthday: getDate({
				// 	format: true
				// }),
				myavatar: "../../static/touxiang/avatar.png"
				//
			};
		},
		onTabItemTap(e) {
			this.getallstorage()
		},
		onReady() {
			this.getallstorage()
		},
		onLoad(option) {
			_self = this
			this.getallstorage()
			this.getMsgList();
			// this.xuguanlist = store.state.iconlib
			
			this.xuguanlist = store.state.iconlib
			// console.log(this.pname);

		},
		onShow(){
			this.scrollTop = 9999999;
			this.getallstorage()
			//模板借由本地缓存实现发红包效果，实际应用中请不要使用此方法。
			//
			uni.getStorage({
				key: 'redEnvelopeData',
				success:  (res)=>{
					// console.log(res.data);
					let nowDate = new Date();
					let lastid = this.msgList[this.msgList.length-1].msg.id;
					lastid++;
					let row = {type:"user",msg:{id:lastid,type:"redEnvelope",time:nowDate.getHours()+":"+nowDate.getMinutes(),userinfo:{uid:0,username:"大黑哥",face:"/static/img/face.jpg"},content:{blessing:res.data.blessing,rid:Math.floor(Math.random()*1000+1),isReceived:false}}};
					this.screenMsg(row);
					uni.removeStorage({key: 'redEnvelopeData'});
				}
			});
		},
		methods:{
			getallstorage:function(){
				uni.getStorage({
					key:"isselecthabit",
					success: (res) => {
						this.isselecthabit = res.data
					}
				})
				uni.getStorage({
					key:"myname",
					success: (res) => {
						this.myname = res.data
					}
				})
				uni.getStorage({
					key:"pname",
					success: (res) => {
						this.pname=res.data	
					},
					fail: (res) => {
						this.pname="陪伴人"
					}
				})
				uni.getStorage({
					key:"myavatar",
					success: (res) => {
							this.myavatar=res.data	
					},
					fail: (res) => {
						this.myavatar="../../../static/touxiang/avatar.png"
					}
				})
				uni.setStorage({
					key:"psex",
					data:this.psex
				})
				uni.setStorage({
					key:"pcallme",
					data:this.pcallme
				})
				uni.setStorage({
					key:"mysex",
					data:this.mysex
				})
				uni.setStorage({
					key:"myIdentity",
					data:this.myIdentity
				})
				uni.setStorage({
					key:"mybirthday",
					data:this.mybirthday
				})
				//获取ta的头像
				uni.getStorage({
					key:"pavatar",
					success: (res) => {
						this.pavatar=res.data	
					},
					fail: (res) => {
						this.pavatar="../../../static/vippage/vipicon/heart.png"
					}
				})
				uni.getStorage({
					key:"uuid",
					success: (res) => {
						this.uuid = res.data
					}
				})
			},
			// 接受消息(筛选处理)
			screenMsg(msg){
				//从长连接处转发给这个方法，进行筛选处理
				if(msg.type=='system'){
					// 系统消息
					switch (msg.msg.type){
						case 'text':
							this.addSystemTextMsg(msg);
							break;
						case 'redEnvelope':
							this.addSystemRedEnvelopeMsg(msg);
							break;
					}
				}else if(msg.type=='user'){
					// 用户消息
					switch (msg.msg.type){
						case 'text':
							this.addTextMsg(msg);
							break;
						case 'voice':
							this.addVoiceMsg(msg);
							break;
						case 'img':
							this.addImgMsg(msg);
							break;
						case 'redEnvelope':
							this.addRedEnvelopeMsg(msg);
							break;
					}

				}
				this.$nextTick(function() {
					// 滚动到底
					this.scrollToView = 'msg'+msg.msg.id
				});
			},
			
			//触发滑动到顶部(加载历史信息记录)
			loadHistory(e){
				if(this.isHistoryLoading){
					return ;
				}
				this.isHistoryLoading = true;//参数作为进入请求标识，防止重复请求
				this.scrollAnimation = false;//关闭滑动动画
				let Viewid = this.msgList[0].msg.id;//记住第一个信息ID
				//本地模拟请求历史记录效果
				setTimeout(()=>{
					
					
					//这段代码很重要，不然每次加载历史数据都会跳到顶部
					this.$nextTick(function() {
						this.scrollToView = 'msg'+Viewid;//跳转上次的第一行信息位置
						this.$nextTick(function() {
							this.scrollAnimation = true;//恢复滚动动画
						});
						
					});
					this.isHistoryLoading = false;
					
				},1000)
			},
			// 加载初始页面消息
			getMsgList(){
				
				let list = [
					{type:"user",msg:{id:1,type:"text",time:"12:56",userinfo:{uid:1,username:"大黑哥",face:"/static/img/face.jpg"},content:{text:this.myname+"你终于来了！木各习惯欢迎你!!我是"+this.pname}}},
					{type:"user",msg:{id:1,type:"text",time:"12:56",userinfo:{uid:1,username:"大黑哥",face:"/static/img/face.jpg"},content:{text:"期待跟你一起变成更优秀的人!哈哈哈!!"}}},
					{type:"user",msg:{id:1,type:"text",time:"12:56",userinfo:{uid:1,username:"大黑哥",face:"/static/img/face.jpg"},content:{text:"点击输入框选择你要打卡的习惯"}}},
					{type:"user",msg:{id:1,type:"text",time:"12:56",userinfo:{uid:1,username:"大黑哥",face:"/static/img/face.jpg"},content:{text:"点击+号可添加新的习惯"}}},
					{type:"user",msg:{id:1,type:"text",time:"12:56",userinfo:{uid:1,username:"大黑哥",face:"/static/img/face.jpg"},content:{text:"我会帮你记录你的习惯历程"}}},
					{type:"user",msg:{id:1,type:"text",time:"12:56",userinfo:{uid:1,username:"大黑哥",face:"/static/img/face.jpg"},content:{text:"我也会陪你一起完成你的习惯计划,记得常来看看我哦~"}}},
					{type:"user",msg:{id:1,type:"text",time:"12:56",userinfo:{uid:1,username:"大黑哥",face:"/static/img/face.jpg"},content:{text:"开始我们的旅程吧..."}}},
				]
				// 获取消息中的图片,并处理显示尺寸
				for(let i=0;i<list.length;i++){
					if(list[i].type=='user'&&list[i].msg.type=="img"){
						list[i].msg.content = this.setPicSize(list[i].msg.content);
						this.msgImgList.push(list[i].msg.content.url);
					}
				}
				this.msgList = list;
				// 滚动到底部
				this.$nextTick(function() {
					//进入页面滚动到底部
					this.scrollTop = 9999;
					this.$nextTick(function() {
						this.scrollAnimation = true;
					});
					
				});
			},
			//处理图片尺寸，如果不处理宽高，新进入页面加载图片时候会闪
			setPicSize(content){
				// 让图片最长边等于设置的最大长度，短边等比例缩小，图片控件真实改变，区别于aspectFit方式。
				let maxW = uni.upx2px(350);//350是定义消息图片最大宽度
				let maxH = uni.upx2px(350);//350是定义消息图片最大高度
				if(content.w>maxW||content.h>maxH){
					let scale = content.w/content.h;
					content.w = scale>1?maxW:maxH*scale;
					content.h = scale>1?maxW/scale:maxH;
				}
				return content;
			},
			
			//更多功能(点击+弹出) 
			showMore(){
				this.isVoice = false;
				this.hideEmoji = true;
				if(this.hideMore){
					this.hideMore = false;
					this.openDrawer();
				}else{
					this.hideDrawer();
				}
			},
			// 打开抽屉
			openDrawer(){
				this.popupLayerClass = 'showLayer';
			},
			// 隐藏抽屉
			hideDrawer(){
				this.popupLayerClass = '';
				setTimeout(()=>{
					this.hideMore = true;
					this.hideEmoji = true;
				},150);
			},
			// 选择图片发送
			chooseImage(){
				this.getImage('album');
			},
			//拍照发送
			camera(){
				this.getImage('camera');
			},

			//选照片 or 拍照
			getImage(type){
				this.hideDrawer();
				uni.chooseImage({
					sourceType:[type],
					sizeType: ['original', 'compressed'], //可以指定是原图还是压缩图，默认二者都有
					success: (res)=>{
						for(let i=0;i<res.tempFilePaths.length;i++){
							uni.getImageInfo({
								src: res.tempFilePaths[i],
								success: (image)=>{
									// console.log(image.width);
									// console.log(image.height);
									let msg = {url:res.tempFilePaths[i],w:image.width,h:image.height};
									this.sendMsg(msg,'img');
								}
							});
						}
					}
				});
			},
			// 选择表情
			chooseXG(){
				this.hideMore = true;
				if(this.hideEmoji){
					this.hideEmoji = false;
					this.openDrawer();
				}else{
					this.hideDrawer();
				}
			},
			//添加表情
			addEmoji(em){
				this.textMsg+=em.alt;
			},
			daka(item){
				this.textMsg=""
				this.textMsg+=item.iconname;
				this.selectedXG='../../../static/iconfont/'+item.url
			},
			delXG(item){
				
				uni.showModal({
					title:"删除习惯",
					content:"确定删除"+item.iconname+"?",
					success: (res) => {
						if(res.confirm){
							uni.showToast({
								icon:"success",
								title:"删除成功"
							})
							this.isselecthabit[item.iconname] = false
							uni.setStorage({
								key:"isselecthabit",
								data:this.isselecthabit
							})
							uni.request({
								url: "http://47.104.95.72:5000/usermanage",
								method: "POST",
								data: [
									this.uuid,
									this.isselecthabit,
									this.pname,
									this.psex,
									this.pcallme,
									this.pavatar,
									this.myname,
									this.mysex,
									this.myIdentity,
									this.mybirthday,
									this.myavatar
							
								]
							})
						}
					}
				})
			},
			clearInput(){
				this.textMsg=""
				this.selectedXG="../../../static/daka.png"
			},
			addxiguan(item){
				
				uni.showModal({
					title:"添加习惯",
					content:"确定添加"+item.iconname+"?",
					success: (res) => {
						if(res.confirm){
							uni.showToast({
								icon:"success",
								title:"添加成功"
							})
							this.isselecthabit[item.iconname] = true
							uni.setStorage({
								key:"isselecthabit",
								data:this.isselecthabit
							})
							uni.request({
								url: "http://47.104.95.72:5000/usermanage",
								method: "POST",
								data: [
									this.uuid,
									this.isselecthabit,
									this.pname,
									this.psex,
									this.pcallme,
									this.pavatar,
									this.myname,
									this.mysex,
									this.myIdentity,
									this.mybirthday,
									this.myavatar
							
								]
							})
						}
					}
				})
			},
			
			//获取焦点，如果不是选表情ing,则关闭抽屉
			textareaFocus(){
				if(this.popupLayerClass=='showLayer' && this.hideMore == false){
					this.hideDrawer();
				}
			},
			// 发送文字消息
			sendText(){
				this.hideDrawer();//隐藏抽屉
				if(!this.textMsg){
					return;
				}
				// let content = this.replaceEmoji(this.textMsg);
				let msg = {text:this.textMsg}
				// console.log("11111"+msg.text);
				this.sendMsg(msg,'text');
				

				
				this.textMsg = '';//清空输入框
			},

			// 发送消息
			sendMsg(content,type){
				var date = new Date();
				var year = date.getFullYear();
				var month = date.getMonth() + 1;
				var day = date.getDate();
				if (month < 10) {
				    month = "0" + month;
				}
				if (day < 10) {
				    day = "0" + day;
				}
				var nowDate = year + "-" + month + "-" + day;
				
				uni.request({
					url:"http://47.104.95.72:5000/yuliao",
					data:{
						date:nowDate,
						iconname:_self.textMsg,
						uuid:_self.uuid
					},
					success: (res) => {
						// console.log("res: " + res.data);
						_self.yuliao=res.data
						// console.log("yuliao1: " + _self.yuliao);
					},
					fail: () => {
						// console.log("fail: ");
					},
					complete: () => {
						var yuliao=_self.yuliao
						
						var tempyuliao=yuliao[0]
						// console.log("tempyuliao___"+tempyuliao);
						//实际应用中，此处应该提交长连接，模板仅做本地处理。
						var temptextMsg=this.textMsg
						var nowDate = new Date();
						let lastid = this.msgList[this.msgList.length-1].msg.id;
						lastid++;
						let msg = {type:'user',msg:{id:lastid,time:nowDate.getHours()+":"+nowDate.getMinutes(),type:type,userinfo:{uid:0,username:"大黑哥",face:"/static/img/face.jpg"},content:content}}
						// 发送消息
						// console.log("msg: " + msg.msg.content);
						this.screenMsg(msg);
						// 定时器模拟对方回复,1秒
						setTimeout(()=>{
							lastid = this.msgList[this.msgList.length-1].msg.id;
							lastid++;
							var msgtype;
							if(yuliao[1]=="character"){
								msgtype="text"
								msg = {type:'user',msg:{id:lastid,time:nowDate.getHours()+":"+nowDate.getMinutes(),type:msgtype,userinfo:{uid:1,username:"售后客服008",face:"/static/img/im/face/face_2.jpg"},content:{text:tempyuliao}}}
								// console.log("msg2: " + msg.msg.content.text);
							}
							if(yuliao[1]=="image"){
								msgtype="img"
								msg = {type:'user',msg:{id:lastid,time:nowDate.getHours()+":"+nowDate.getMinutes(),type:msgtype,userinfo:{uid:1,username:"售后客服008",face:"/static/img/im/face/face_2.jpg"},content:{url:tempyuliao}}}
								// console.log("msg2: " + msg.msg.content.url);
							}
							// console.log("msgtype: " + msgtype);
							// console.log("msg2: " + msg);
							this.screenMsg(msg);
							
						},1000)
					}
				})
				
			},
			
			// 添加文字消息到列表
			addTextMsg(msg){
				this.msgList.push(msg);
			},
			// 添加语音消息到列表
			addVoiceMsg(msg){
				this.msgList.push(msg);
			},
			// 添加图片消息到列表
			addImgMsg(msg){
				msg.msg.content = this.setPicSize(msg.msg.content);
				this.msgImgList.push(msg.msg.content.url);
				this.msgList.push(msg);
			},
			addRedEnvelopeMsg(msg){
				this.msgList.push(msg);
			},
			// 添加系统文字消息到列表
			addSystemTextMsg(msg){
				this.msgList.push(msg);
			},
			// 添加系统红包消息到列表
			addSystemRedEnvelopeMsg(msg){
				this.msgList.push(msg);
			},
			
			sendSystemMsg(content,type){
				let lastid = this.msgList[this.msgList.length-1].msg.id;
				lastid++;
				let row = {type:"system",msg:{id:lastid,type:type,content:content}};
				this.screenMsg(row)
			},
			// 预览图片
			showPic(msg){
				uni.previewImage({
					indicator:"none",
					current:msg.content.url,
					urls: this.msgImgList
				});
			},

			// 切换语音/文字输入
			switchVoice(){
				this.hideDrawer();
				this.isVoice = this.isVoice?false:true;
			},
			discard(){
				return;
			}
		}
	}
</script>
